mapper.update:
update user

    username = #{username},
    realname = #{realname},
    updator = #{updator},
    updatetime = #{updatetime}

where id = #{id}

update user

    isdel = #{isdel},
    updator = #{updator}

WHERE id = #{id}

mapper.select:
select id,groupname,groupuser,groupphone,legalize,status,contract_end_time contractEndTime from hr_group


        or groupname like concat('%',#{groupname},'%')


        and legalize = #{legalize}


        and status = #{status}


        and isdel = #{isdel}


order by updatetime desc
